        /// <returns></returns>
        private async Task<Result<ValidateResult>> OrderDetailValidate(OTADetailInput input)
        {
            ValidateResult result = new ValidateResult();


            ////必须先验证公园编号
            ////验证公园编号
            //var parkCodeResult = await ValidateParkCode(input);
            //if (!parkCodeResult.Success)
